import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState(0)
  const [searchName, setSearchName] = useState('')

  const addPerson = (event) => {
    event.preventDefault()

    if (persons.find( person => person.name === newName) === undefined) {
      const personObject = {
        name: newName,
        number: newNumber,
        id: persons.length + 1
      }
      setPersons(persons.concat(personObject))
      setNewName('')
    }
    else {
      alert(`${newName} is already added to phonebook.`)
    }
  }

  const showMatches = () => {
    return persons.filter(person => person.name.toLowerCase().search(searchName) !== -1)
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearchName = (event) => {
    setSearchName(event.target.value)
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
          filter by name: <input value={searchName} onChange={handleSearchName}/>
        </div>
      </form>
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName} onChange={handleNameChange} />
        </div>
        <div>
          number: <input value={newNumber} onChange={handleNumberChange} />
        </div>
        <div>
          <button type={"submit"}>add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {
        showMatches().map((person) => (
            <div key={person.id}>{person.name} @ {person.number}</div>
          )
        )
      }
      {/* <div>debug: {newName}</div>  render state as text for debugging purposes */}
    </div>
  )
}

export default App
