import { useState } from 'react'

import { Filter } from './components/Filter'
import { PersonForm } from './components/PersonForm'
import { Persons } from './components/Persons'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState(0)
  const [searchName, setSearchName] = useState('')

  const addPerson = (event) => {
    event.preventDefault()

    if (persons.find( person => person.name === newName) === undefined) {
      const personObject = {
        name: newName,
        number: newNumber,
        id: persons.length + 1
      }
      setPersons(persons.concat(personObject))
      setNewName('')
    }
    else {
      alert(`${newName} is already added to phonebook.`)
    }
  }

  const showMatches = () => {
    return persons.filter(person => person.name.toLowerCase().search(searchName) !== -1)
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearchName = (event) => {
    setSearchName(event.target.value)
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <Filter searchName={searchName} handleSearchName={handleSearchName}/>
      <PersonForm state={{newname: newName, newNumber: newNumber}} handleState={{handleNameChange: handleNameChange, handleNumberChange: handleNameChange}} />
      <h2>Numbers</h2>
      <Persons person={persons}/>
      {/* <div>debug: {newName}</div>  render state as text for debugging purposes */}
    </div>
  )
}

export default App
